#!/usr/bin/python# -*- coding:utf-8 -*-import stringclass Solution:    @staticmethod    def get_char(*args):        for lowercase in string.ascii_lowercase:            if lowercase in args:                continue            return lowercase    @staticmethod    def replace_string(input_str: str) -> str:        length = len(input_str)        if length == 1:            return input_str if input_str != '#' else 'a'        result = []        for index, char in enumerate(input_str):            if char != '#':                result.append(char)                continue            elif index == 0:                result.append(Solution.get_char(input_str[index + 1]))            elif index == (length - 1):                result.append(Solution.get_char(result[-1]))            else:                result.append(Solution.get_char(result[-1], input_str[index + 1]))        return ''.join(result)if __name__ == '__main__':    input_string = "m#efy###am"    print(Solution.replace_string(input_string))