#!/usr/bin/python# -*- coding:utf-8 -*-from types import MethodTypeclass Solution:    def instance_method(self) -> int:        pass    @classmethod    def cls_method(cls) -> int:        return 1    @staticmethod    def static_method() -> int:        return 1if __name__ == '__main__':    print("instance type for instance method: ", type(Solution().instance_method) is MethodType)    print("instance type for class method: ", type(Solution().cls_method) is MethodType)    print("instance type for static method: ", type(Solution().static_method) is MethodType)    print()    print("class type for instance method: ", type(Solution.instance_method) is MethodType)    print("class type for class method: ", type(Solution.cls_method) is MethodType)    print("class type for static method: ", type(Solution.static_method) is MethodType)    print()    print("class type for instance method: ", type(Solution().instance_method))    print("class type for class method: ", type(Solution().cls_method))    print("class type for static method: ", type(Solution().static_method))